@namespace url(http://www.w3.org/1999/xhtml);
/*
 * Basic layout
 */

.container {
  width: 100% !important;
  padding-right: 30px !important;
  padding-left: 30px !important;
}

/* Responsive containers on some pages */
.container-lg,
.container-xl {
  max-width: 100% !important;
  padding-right: 30px !important;
  padding-left: 30px !important;
}

/* Match the header to container padding */
.Header {
  padding-right: 30px !important;
  padding-left: 30px !important;
}


/*
 * Profile pages
 */

.pinned-repo-item {
  width: calc(50% - 10px) !important;
}
.org-pinned-repos-list .pinned-repo-item {
  width: calc(33.3% - 10px) !important;
}

.u-photo,
.user-status-container {
  max-width: 250px;
}
.u-photo .avatar {
  width: 100% !important;
  height: auto !important;
}


/*
 * Repo-specific stuff
 */

.repository-content {
  width: 100% !important;
}

/* Undo container changes for the Projects tab on repos which have unnecessary nested container-lg's */
.repository-content > .container-lg {
  padding-right: 0 !important;
  padding-left: 0 !important;
}

/* Undo for profile timeline */
.contribution-activity-listing .discussion-timeline {
  width: 100% !important;
}

/* Fix #18 - props: @auscompgeek */
.file-header::after {
  clear: left !important;
}

/* Network graph */
#network {
  max-width: 730px;
}


/*
 * Issues & Dashboard
 */

#dashboard,
.new-issue-form {
  position: relative !important;
}

.new-issue-form .discussion-sidebar {
  position: absolute !important;
  top: 0 !important;
  right: 0 !important;
}

button.discussion-sidebar-toggle {
  width: 100% !important;
}

.timeline-new-comment {
  max-width: none !important;
}

/* Commits: extended message under "..." */
.commit-desc pre {
  max-width: none;
}

/* Alert messages */
#js-flash-container .flash-messages {
  width: 100% !important;
  padding-left: 30px !important;
  padding-right: 30px !important;
}

a {
    color: #2456A4 !important;
}
strong {
    color: #6392BF;
}
em {
    color: #A9312A;
    font-style: normal !important;
}
table {
    font-size: 90% !important;
}

#jp-main-dock-panel {
    background-color: #f9f9f9;
}
.jp-RenderedHTMLCommon {
    font-family: "Yuanti SC";
    font-size: 100%;
}
.jp-Notebook {
    background-color: #fbfafa;
}
.CodeMirror,
.jp-RenderedHTMLCommon pre {
    font-size: 90%;
}
.jp-RenderedHTMLCommon pre {
    padding: 10px 25px;
    background-color: #fafafa;
    border-left: 4px solid #dadada;
    border-radius: 10px;
}

.jp-RenderedHTMLCommon pre code {
    background-color: #fafafa;
}

.jp-RenderedHTMLCommon h1 code,
.jp-RenderedHTMLCommon h2 code,
.jp-RenderedHTMLCommon h3 code,
.jp-RenderedHTMLCommon h4 code,
.jp-RenderedHTMLCommon p code,
.jp-RenderedHTMLCommon li code,
.jp-RenderedHTMLCommon blockquote p code,
.jp-RenderedHTMLCommon blockquote li code,
.jp-RenderedHTMLCommon td code {
    background-color: #f6f6f6;
    font-size: 90%;
    color: #2e2e2e;
    padding: 4px 4px;
    margin: 0 8px;
    box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.2);
    border-radius: 4px;
}

.markdown-body {
    font-family: "PingFang SC";
}
strong {
    color: #6392BF;
}
em {
    color: #A9312A;
    font-style: normal !important;
}
table {
    font-size: 95% !important;
}


.CodeMirror,
pre {
    font-size: 90%;
}
pre {
    padding: 10px 25px;
    background-color: #fafafa;
    border-left: 4px solid #dadada;
    border-radius: 10px;
}

pre code {
    background-color: #fafafa;
}

h1 code,
h2 code,
h3 code,
h4 code,
p code,
li code,
blockquote p code,
blockquote li code,
td code {
    background-color: #f6f6f6;
    font-size: 90%;
    color: #2e2e2e;
    padding: 4px 4px;
    margin: 0 8px;
    box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.2);
    border-radius: 4px;
}
